import React, {Component} from "react";
import style from './Tasks.module.css';

const Tasks = ({listItems}) => {

            if(listItems.length > 0){
                return(
                    <ul className={style.ul}>
                        {
                            listItems.map((val, index) =>{
                                return <div className={style.itemContainer} key={index}>
                                            <li className={style.li} onClick={this.completedItem} >
                                            <span className={style.triangle}></span>{val}</li>
                                            <span onClick={this.removeItem.bind(this, index)} className={style.delete}>X</span>
                                        </div>
                            })
                        }
                    </ul>
                );
            }else{
                return(
                    <ul className={style.ul}>
                        <li className={style.li} >No Tasks Scheduled</li>
                    </ul>

                );
            }
}
export default Tasks;
