import React, { Component } from 'react';
import Form from '../component/Form/Form';
import ToDoButton from '../component/ToDoButton/ToDoButton';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state = {
      completed: 0,
      currentInput: "Add items",
      listItems: [],
      percentage: `0%`,
      numOfTasks: 0,
    }
  }

  //Captures current state of input field
  inputChange = (e) =>{
    this.setState({currentInput: e.target.value});
  }

  //Add tasks to list
  addListItem = (e) => {
    e.preventDefault();
    if(this.state.currentInput.length > 0 && this.state.currentInput !== 'Ad items'){
    let currentListItems = this.state.listItems;
    currentListItems.push({value: this.state.currentInput, done: false});
      this.setState((prevState)=>{
        return {listItems: currentListItems, numOfTasks: this.state.numOfTasks + 1}
      })
    }
  }

  //Removes tasks from list
  removeItem = (i) =>{
    const filteredState = this.state.listItems.filter((val, index) =>{
      return index !== i;
    })
    this.setState({listItems: filteredState, numOfTasks: this.state.numOfTasks - 1});
  }

  //Adds a strike through list items when completed
  completedItem = (i) =>{
    let currentListItems = this.state.listItems;
    let getCompletedItem = this.state.listItems.filter((val, index) =>{
      return index === i;
    });

    if(getCompletedItem[0].done === true){
      getCompletedItem[0].done  = false;
    }else{
      getCompletedItem[0].done = true;
    }
    currentListItems[i] = getCompletedItem[0];
    this.setState({listItems: currentListItems});
    // this.getProgressPercentage();
  }

  //Clears input field on focus
  clearFocus = (e) =>{
    this.setState({currentInput: ""});
  }

  // progressStatus = () =>{
  //   const completedTask = this.state.listItems.filter(val =>{
  //     return val.done = true;
  //   });
    // this.setState({completed: completedTask.length});

    // this.getProgressPercentage();
  // }

  // getProgressPercentage = () =>{
  //   this.setState({percentage: 100 * this.state.completed / this.state.numOfTasks});
  // }

  render() {
    return (
      <div className="App">
      <Form 
            numOfTasks={this.state.numOfTasks}
            clearFocus={this.clearFocus}
            completed={this.state.completed}
            currentInput={this.state.currentInput}
            inputChange={this.inputChange} 
            listItems={this.state.listItems}
            percentage={this.state.percentage}
            addListItem={this.addListItem}
            completedItem={this.completedItem}
            removeItem={this.removeItem}/>
      </div>
    );
  }
}

export default App;
