import React, { Component } from 'react';
import Form from '../component/Form/Form';
import ToDoButton from '../component/ToDoButton/ToDoButton';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state = {
      clearInput: "",
      currentInput: "",
      listItems: []
    }
  }

  inputChange = (e) =>{
    this.setState({currentInput: e.target.value.toUpperCase()});
  }

  addListItem = (e) => {
    e.preventDefault();
    let currentListItems = this.state.listItems;
    currentListItems.push(this.state.currentInput);
    if(currentListItems.length > 0){
      this.setState((prevState)=>{
        return {listItems: currentListItems, currentInput: ""}
      })
    }
  }

  removeItem = (index) =>{
    let currentListItems = this.state.listItems;
    console.log(currentListItems, index);
  }
  render() {
    return (
      <div className="App">
      <Form clearInput={this.state.clearInput}
            currentInput={this.state.currentInput}
            inputChange={this.inputChange} 
            listItems={this.state.listItems}
            addListItem={this.addListItem}
            editListItem={this.editListItem}
            removeItem Item={this.removeItem}/>
      </div>
    );
  }
}

export default App;
