import React from 'react';
import style from './FormInput.module.css'


const FormInput = ({inputChange, currentInput, clearInput}) => {
    return(
        <input onChange={inputChange} onFocus={this.value = ""} className={style.input} type="text" placeholder="Add items"/>
    );
}

export default FormInput;
