import React, { Component } from 'react';
import Form from '../component/Form/Form';
import ToDoButton from '../component/ToDoButton/ToDoButton';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state = {
      clearInput: "",
      currentInput: "",
      listItems: []
    }
  }

  inputChange = (e) =>{
    this.setState({currentInput: e.target.value.toUpperCase()});
  }

  addListItem = (e) => {
    e.preventDefault();
    this.setState({listItems:{value: this.state.currentInput, done: false}});
    // if(this.state.currentInput.length > 0){
    // let currentListItems = this.state.listItems;
    // currentListItems.push(this.state.currentInput);
    //   this.setState((prevState)=>{
    //     return {listItems: {value: currentListItems, done: false}}
    //   })
    // }
    // console.log(this.state.listItems)  
  }

  removeItem = (i) =>{
    const filteredState = this.state.listItems.filter((val, index) =>{
      return index !== i;
    })
    this.setState({listItems: filteredState});
  }

  completedItem = (e) =>{
    console.log(e);
  }
  render() {
    return (
      <div className="App">
      <Form clearInput={this.state.clearInput}
            currentInput={this.state.currentInput}
            inputChange={this.inputChange} 
            listItems={this.state.listItems}
            addListItem={this.addListItem}
            completedItem={this.completedItem}
            removeItem={this.removeItem}/>
      </div>
    );
  }
}

export default App;
