import React, { Component } from 'react';
import Form from '../component/Form/Form';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state = {
      currentInput: "",
      listItems: []
    }
  }

  inputChange = (e) =>{
    this.setState({currentInput: e.target.value});
    console.log(this.state.currentInput);
  }
  render() {
    return (
      <div className="App">
      <Form inputChange={this.inputChange} listItems={this.state.listItems}/>
      </div>
    );
  }
}

export default App;
