import React from 'react';
import style from './FormInput.module.css'


const FormInput = ({clearFocus, inputChange, currentInput, clearInput}) => {
    return(
        <input onChange={inputChange}  value={currentInput} className={style.input} type="text" placeholder="Add items"/>
    );
}

export default FormInput;
