import React, {Component} from "react";
import style from './Tasks.module.css';

class Tasks extends Component {
    constructor(props){
        super();
        this.state = {
            completed: true
        }
    }


  editListItem = () => {

}

completedItem = (e) => {
    if(this.state.completed === true){
        this.setState({completed: false});
    }else{
        this.setState({completed: true});
    }
}

    render(){
            if(this.props.listItems.length > 0){
                return(
                    <ul className={style.ul}>
                        {
                            this.props.listItems.map((val, index) =>{
                                return <div key={index}><li onClick={this.completedItem} className = {this.state.completed ? style.li : style.completed} ><span className={style.triangle}></span>{val}</li></div>
                            })
                        }
                    </ul>
                );
            }else{
                return(
                    <ul className={style.ul}>
                        <li className={style.li} >No Tasks Scheduled</li>
                    </ul>

                );
            }
        }
}
export default Tasks;
